import React from 'react'




const VisibilityControl = ({ setShowCompleted, cleanTasks }) => {

  const handleDdelete = () => {
    // alert('limpiando')
    // preguntamos al usuario si quiere eliminarlo 
    if (window.confirm('Are you sureypu want to delete it?')) {
      cleanTasks()
    }
  }

  return (
    <div>
      <input type='checkbox' onChange={e => setShowCompleted(e.target.checked)} /> <label>Show task Done</label>
      {/* // creamos un boton cuando se le de onClick lanzara una especie de funcion que limpiara toda clase de elementos
      // la crearemos arriba com handleDelete */}
      <button onClick={handleDdelete}>
        Clear
      </button>
    </div>
  )
}

export default VisibilityControl
