// ahora en una tabla mostrare solo las tareas por hacer 
// 
import React from 'react';
import TaskRow from './TaskRow';



const TaskTable = ({ tasks, toggleTask, showCompleted }) => {

  const taskTableRow = (doneValue) => { // pasamos como parametro doneValue y lo vemos por consola
    console.log(doneValue) //para recibirlo, en la funcion que pusimos en tabla pasasmos true
    // asi le puedo pasar un parametro, pero lo dejaremos en false para que la tabla miestre las tareas que no estan hechas
    // para ello antes de retornar las tareas aplicaremos un filtro, esto sera antes del map
    // recordar que los metodos de los arreglos los puedo encadenar
    // le diremos que filtre por cada una de las tareas en su propiedad done si es === a true , asi mantendra todas las tareas donde sea true
    return (
      tasks
        .filter(task => task.done === doneValue)
        .map(task => (
          < TaskRow
            task={task}
            key={task.name}
            toggleTask={toggleTask}
          />
        )))
  } //lo que haremos es que basados en una propiedad cambiaremos lo valores

  return (
    <table>
      <thead>
        <tr>
          <th>Tareas</th>
        </tr>
      </thead>
      <tbody>
        {
          // tasks.map(task => (
          //   < TaskRow
          //     task={task}
          //     key={task.name}
          //     toggleTask={toggleTask}
          //   />
          // ))

          // lo primero quqe haremos es que esta logica que teniamos la llevaremos a una funcion aparte y para usarlo llamo a ala funcion
          // la funcion sigue funcionando exactamente igual ahora trabajamos en la misma funcion 
          taskTableRow(showCompleted)
        }
      </tbody>
    </table>
  )
}

export default TaskTable
