//hremos que las tareas que se estan guardando e ne este estado cambien es su propiedad en su propiedad done de false a true
//  antes haremos un componente para la tabla
// luego aca cambiaremos el estado ya que estan todas las tareas para ello crearemos una funcion para actualizar una nueva tarea
// la llamaremos toggleTask

import { useEffect, useState } from "react";
import "./App.css";
import TaskCreator from "./components/TaskCreator";
import TaskTable from "./components/TaskTable";


function App() {
  const [tasksItem, setTasksItem] = useState([])

  function createNewTask(taskName) {
    if (!tasksItem.find(task => task.name === taskName)) {
      setTasksItem([...tasksItem, { name: taskName, done: false }])
    }
  }

  // esta funcion recibira una tarea y la buscaremos dentro de nuestroe elementos para cambiarlo
  const toggleTask = task => {  //task es el valor que me estan pasando
    tasksItem.map(t => (t.name === task.name) ? { ...t, done: !t.done } : t) //t es cada recorrido
  }

  useEffect(() => {
    let data = localStorage.getItem('task')
    if (data) {
      setTasksItem(JSON.parse(data))
    }
  }, [])

  useEffect(() => {
    localStorage.setItem('task', JSON.stringify(tasksItem))
  }, [tasksItem])

  // en todos estor return retornamos una porcion de html
  return (
    <div className="App">
      <TaskCreator createNewTask={createNewTask} />
      <TaskTable tasks={tasksItem} />

    </div>
  );
}

export default App;
