import { useState } from "react";
import "./App.css";
import TaskCreator from "./components/TaskCreator";

function App() {
  const [tasksItem, setTasksItem] = useState([
    { name: 'mi primer tarea', done: false },
    { name: 'mi segunda tarea', done: false },
    { name: 'mi tercera tarea', done: false },
  ])//este arreglo vacio lo quiero mostrar en el return debajo de taskCreator para ello agregare algunas tareas y tengo que recorrerlas 
  // 

  function createNewTask(taskName) {
    // alert(taskName)
    if (!tasksItem.find(task => task.name === taskName)) {
      setTasksItem([...tasksItem, { name: taskName, done: false }])
    }
    // guardamos la nueva tarea
    // setTasksItem([...tasksItem, { name: taskName, done: false }]) 
    // pasamos esto al if
  }


  return (
    <div className="App">
      <TaskCreator createNewTask={createNewTask} />
      {/* aca guardamos tareas en el localstorage y de aqui tambien queremos añadirloa a las lista de tareas
      para ello debemos pasar una funcion desde app a taskCreator para que este lo ejecute y pase el nuevo dato
      creamos la funcion createNewTask  y la pasamos a taskCreator como props
      react puede recibir datos, string y funciones  en los componentes*/}


      <table>
        <thead>
          <tr>
            <th>Tareas</th>
          </tr>
        </thead>
        <tbody>
          {
            tasksItem.map(task => (
              <tr key={task.name}>
                <td>{task.name}</td>
              </tr>
            ))
          }
          {/* pero como añado una nueva tarea a esta lista? */}
        </tbody>
      </table>



    </div>
  );
}

export default App;
