import { useState } from "react";
import "./App.css";
import TaskCreator from "./components/TaskCreator";

function App() {
  const [tasksItem, setTasksItem] = useState([
    { name: 'mi primer tarea', done: false },
    { name: 'mi segunda tarea', done: false },
    { name: 'mi tercera tarea', done: false },
  ])//este arreglo vacio lo quiero mostrar en el return debajo de taskCreator para ello agregare algunas tareas y tengo que recorrerlas 
  // 
  return (
    <div className="App">
      <TaskCreator />


      <table>
        <thead>
          <tr>
            <th>Tareas</th>
          </tr>
        </thead>
        <tbody>
          {
            tasksItem.map(task => (
              <tr key={task.name}>
                {task.name}
              </tr>
            ))
          }
        </tbody>
      </table>



    </div>
  );
}

export default App;
