// necesito traer el arreglo de tareas y la recibire mediante props 
//  y en el map solo lo llamaremos tareas

import React from 'react'


const TaskTable = ({ tasks }) => {
  return (
    <table>
      <thead>
        <tr>
          <th>Tareas</th>
        </tr>
      </thead>
      <tbody>
        {
          // tasksItems.map(task => (
          tasks.map(task => (
            <tr key={task.name}>
              <td>
                {task.name}
                <input
                  type='checkbox'
                  checked={task.done} />
              </td>
            </tr>
          ))
        }
        {/* pero como a√±ado una nueva tarea a esta lista? */}
      </tbody>
    </table>
  )
}

export default TaskTable
