import React from 'react'

const TaskRow = ({ task, toggleTask }) => {
  return (
    <tr>
      <td>
        {task.name}
        <input
          type='checkbox'
          checked={task.done}
          onChange={() => toggleTask(task)}
        />
      </td>
    </tr>
  )
}

export default TaskRow
