import { useState } from "react";
import React from "react";

const TaskCreator = () => {
  const [newTaskName, setnewTaskName] = useState();

  const handleSubmit = (e) => {
    e.preventDefault(); // cancela el efecto de enviar datos al backend
    localStorage.setItem("task", newTaskName);
    setnewTaskName('');// aca limpio mi input despues guardar la informacion
  };

  return (
    <form onSubmit={handleSubmit}>

      <input
        type="text"
        placeholder="Enter a new task"
        value={newTaskName} //muestra el valor del estado que es vacio que limpio despues de guardar la informacion
        onChange={(e) => {
          setnewTaskName(e.target.value);
        }}
      />
      <button>
        Save task
      </button>
    </form>
  );
};

export default TaskCreator;
