import { useState } from "react";
import "./App.css";
import TaskCreator from "./components/TaskCreator";

function App() {
  const [tasksItem, setTasksItem] = useState([
    { name: 'mi primer tarea', done: false },
    { name: 'mi segunda tarea', done: false },
    { name: 'mi tercera tarea', done: false },
  ])

  function createNewTask(taskName) {
    if (!tasksItem.find(task => task.name === taskName)) {
      setTasksItem([...tasksItem, { name: taskName, done: false }])
    }
  }

  return (
    <div className="App">
      <TaskCreator createNewTask={createNewTask} />
      <table>
        <thead>
          <tr>
            <th>Tareas</th>
          </tr>
        </thead>
        <tbody>
          {
            tasksItem.map(task => (
              <tr key={task.name}>
                <td>{task.name}</td>
              </tr>
            ))
          }
          {/* pero como a√±ado una nueva tarea a esta lista? */}
        </tbody>
      </table>
    </div>
  );
}

export default App;
