import React from 'react'

const TaskRow = ({ task, toggleTask }) => {
  return (
    // < key={task.name}>// ya no uso key por que lo esta usando el componenete al momento de ser llamado
    <tr>
      <td>
        {task.name}
        <input
          type='checkbox'
          checked={task.done}
          onChange={() => toggleTask(task)}//pasamos la tarea que estamos intentado cambiar el estado

        />
      </td>
    </tr>
  )
}

export default TaskRow
