import { useState } from "react";
import "./App.css";

function App() {
  //const [variable, funcion que me permite actualizar la variable] = useState()
  // entonces para llenar esta variable usamos la funcion setnewTaskName(ver onChange)
  const [newTaskName, setnewTaskName] = useState()

  const handleSubmit = (e) => {
    e.preventDefault()// cancela el efecto de enviar datos al backend 
    alert('sended')
  }

  return (
    <div className="App">

      <form onSubmit={handleSubmit} >  {/* si al boton le saco onClick el boton solo enviaria el evento del formulario
      que es el onsubmit(evento de envio a un backend, pero ene este caso como no tenemos back lo usaremos para capturar los datos
      antes de que se envien)  y como la pagina se refersca entonces para capturar este evento lo hacemoss en una funcion aparte handleSubmit*/}

        <input
          type="text"
          placeholder="Enter a new task"
          onChange={(e) => { // cuando el usuario tipee el valor lo guardare en una variable para usarlo despues llamando onChange
            // asi capturo lo que se escribe en este input pero aun no lo guardo para ello lo tengo que guardar en un estado
            // (viene de 7)entonces aca  a medoda que tipees llenaras la variable con la funcion setnewtaskname
            setnewTaskName(e.target.value);
          }}
        />
        <button
          onClick={() => { //capturando el evento click que me sirve para cuando el usuario haga click yo vea el valor del input
            alert(newTaskName);
          }}
        >
          Save task
        </button>
      </form>
    </div>
  );
}

export default App;
